import React from 'react';
import {FirebaseContext} from '../firebase';

class Admin extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            statusStr: "Status: no action yet taken"
        };
    }
    setStatus(newStatus)
    {
        this.setState({statusStr: "Status: "+newStatus});
    }
    render(){
        return(
        <div>
            <div>
                <p>
                   Page only for admins to see
                </p>
                <p id="statusInformer">{this.state.statusStr}</p>
            </div>

            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addAdmin" onClick={()=>{firebase.doAddAdmin("daniel.loyd19@gmail.com", this.setStatus.bind(this));}}>Add an admin</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delAdmin" onClick={()=>{firebase.doDeleteAdmin("test.AdminsEmail@gmail.com", this.setStatus.bind(this))}}>Delete an admin</button>
                }
            </FirebaseContext.Consumer>
            <div></div>
            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addUser" onClick={()=>{firebase.doAddUser("test.UserEmails@gmail.com", this.setStatus.bind(this));}}>Add an User</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delUser" onClick={()=>{firebase.doDeleteUser("test.UserEmails@gmail.com", this.setStatus.bind(this))}}>Delete an User</button>
                }
            </FirebaseContext.Consumer>
            </div>
        );
    }
}

export default Admin;
