import React from 'react';
import ReactDOM from 'react-dom';
import {createStore, combineReducers, applyMiddleware} from "redux";
import logger from "redux-logger"
import {Provider} from "react-redux"

import './index.css';
import App from './components/app';
import * as serviceWorker from './serviceWorker';
import Firebase, {FirebaseContext} from './components/firebase';

const intialState = {userSignedIn: false,
    user: "",
    userName: ""};
const userAuthReducer = (state=intialState, action) => {
    //state takes in an intial state at program intialization, all other calls will have a state input
    var newState = {...state};

    switch(action.type)
    {
        case "SIGNIN":
            newState.user = action.payload.email;
            newState.userName = action.payload.userName;
            newState.userSignedIn = true;
            break;
        case "SIGNOUT":
            newState.userSignedIn = false;
            newState.user = "";
            newState.userName = "";
            break;
        default:
            break;
    }
    return newState;
};
const otherReducer = (state, action) => {
    //state takes in an intial state at program intialization, all other calls will have a state input
    var newState = {...state};

    switch(action.type)
    {
