import React from 'react';
import { connect } from 'react-redux';
import Firebase, { FirebaseContext } from '../firebase';
import SignIn from '../signIn';
import SignOut from '../signOut';
import Admin from '../admin';
import Account from '../account';


class Home extends React.Component{
    constructor(){
        super();
        this.state = {
            userSignedIn: false,
            user: "",
            userName: "",
            isAdmin: false
        };
    }
    signInUser(newUser, adminStatus){
        this.props.userDispatch({email: "test", userName: "connected"});
        this.setState({
            userSignedIn: true,
            user: newUser.email,
            userName: newUser.name,
            isAdmin: adminStatus
        });
    }
    signOutUser(){
        this.setState({
            userSignedIn: false,
            user: "",
            userName: "",
            isAdmin: false
        });
    }
    render(){
        var adminPage = ""
        var accountPage = ""
        console.log("props: ", this.props);
        if(this.state.userSignedIn)
        {
            accountPage = <Account userName = {this.state.userName} userEmail={this.state.user}></Account>
        }
        if(this.state.isAdmin)
        {
            adminPage = <Admin />
        }
        return (
            <div>
                <FirebaseContext.Consumer>
                    {
                        firebase => 
                            <SignIn firebase = {firebase} signInBind = {this.signInUser.bind(this)}/>

                    }
                </FirebaseContext.Consumer>
                <FirebaseContext.Consumer>
                    {
                        firebase => 
                            <SignOut firebase = {firebase} signOutBind = {this.signOutUser.bind(this)}/>

                    }
                </FirebaseContext.Consumer>
                {accountPage}
                {adminPage}
            </div>

        );
    }
}

//put this function call in react components
const mapStateToProps = (state) => {
	//return object with key value pairs that this component can use
	// localPropName: globalStateNameFromRedux
	return {
        userTest: state.userAuthReducer
	};
};
const mapDispatchToProps = (dispatch) => {
	//return object with key value pairs that this component can use
	// localPropName: globalStateNameFromRedux
	return {
            userDispatch: (userObj) => {dispatch({
                type: "SIGNIN",
                payload: userObj
            });
        }
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
