import React from 'react';
import ReactDOM from 'react-dom';
import {createStore, combineReducers} from "redux";
import logger from "redux-logger"
import {Provider} from "react-redux"
import thunk from 'redux-thunk';
import './index.css';
import App from './components/app';
import * as serviceWorker from './serviceWorker';
import Firebase, {FirebaseContext} from './components/firebase';

const initialState = {userSignedIn: false,
    email: "",
    userName: ""};
const userAuthReducer = (state=initialState, action) => {
    //state takes in an intial state at program intialization, all other calls will have a state input
    var newState = {...state};

    switch(action.type)
    {
        case "SIGNIN":
            newState.email = action.payload.email;
            newState.userName = action.payload.userName;
            newState.userSignedIn = true;
            newState.isAdmin = action.payload.isAdmin;
            break;
        case "SIGNOUT":
            newState.userSignedIn = false;
            newState.isAdmin = false;
            newState.email = "";
            newState.userName = "";
            break;
        default:
            console.log("error in userAuthReducer, unknown action type");
            break;
    }
    return newState;
};
const initialFieldState = {responseField: [],
                            issueField: [],
                            userField: [],
                            adminField: [],
                            programField: [],
                            status: "no action taken"
                            };
const fieldReducer = (state=initialFieldState, action) => {
    var newState = {...state};

    switch(action.type)
    {
        case "UPDATESTATUS":
            newState.status = action.payload;
            break;
        case "":

            break;
        case "":

            break;
        default:
            break;
    }
    return newState;
};
const initialSearchState = {responseQuery: "",
                            issueQuery:"",
                            userQuery:"",
                            adminQuery: "",
                            programQuery: ""
                            };
const searchReducer = (state=initialSearchState, action) => {
    var newState = {...state};

    switch(action.type)
    {
        case "NEWSEARCH_RESPONSE":
            newState.responseQuery = action.payload;
            break;
        case "NEWSEARCH_ISSUE":
            newState.issueQuery = action.payload;
            break;
        case "NEWSEARCH_USER":
            newState.userQuery = action.payload;
            break;
        case "NEWSEARCH_ADMIN":
            newState.adminQuery = action.payload;
            break;
        case "NEWSEARCH_PROGRAM":
                newState.programQuery = action.payload;
                break;
        case "CLEARALLSEARCHES":
            newState = {responseQuery: "",
            issueQuery:"",
            userQuery:"",
            adminQuery: "",
            programQuery: ""
            };
            break;
        default:
            break;
    }
    return newState;
};
const store = createStore(combineReducers({userAuthReducer, fieldReducer, searchReducer}));
store.subscribe(()=>{console.log(store.getState())});

ReactDOM.render(
    <Provider store={store}>
        <FirebaseContext.Provider value={new Firebase()}>
            <App />
        </FirebaseContext.Provider>
    </Provider>, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
