import React from 'react';
import { disconnect } from 'cluster';

class Fields extends React.Component
{
    render(){
        return(
            <div>
                <button id="addAdmin" onClick={()=>{this.props.firebase.doAddAdmin("test.AdminsEmail@gmail.com", this.setStatus.bind(this));}}>Add an admin</button>
                <button id="delAdmin" onClick={()=>{this.props.firebase.doDeleteAdmin("test.AdminsEmail@gmail.com", this.setStatus.bind(this))}}>Delete an admin</button>
                <button id="addUser" onClick={()=>{this.props.firebase.doAddUser("test.UserEmails@gmail.com", this.setStatus.bind(this));}}>Add an User</button>
                <button id="delUser" onClick={()=>{this.props.firebase.doDeleteUser("test.UserEmails@gmail.com", this.setStatus.bind(this))}}>Delete an User</button>
                <button id="addIssue" onClick={()=>{this.props.firebase.doAddIssue("test issue", this.setStatus.bind(this));}}>Add an issue</button>
                <button id="delIssue" onClick={()=>{this.props.firebase.doDeleteIssue("test issue", this.setStatus.bind(this))}}>Delete an issue</button>
                <div/>
                <hr/>
                <p>Responses</p>
                <input id="resSearchInput"type="text" placeholder="search" onChange={(event)=>{this.setSearch("response",event)}}></input>
                <div />
                <button id="addResponse" onClick={()=>{this.props.firebase.doAddResponse("test response", this.setStatus.bind(this));}}>Add a response</button>
                <button id="delResponse" onClick={()=>{this.props.firebase.doDeleteResponse("test response", this.setStatus.bind(this))}}>Delete a response</button>
                <button id="searchResponse" onClick={()=>{this.props.firebase.doSearch({type:"response", substr:window.document.getElementById("resSearchInput").value, updateListBind: this.updateList.bind(this)}, this.setStatus.bind(this))}}>search responses</button>
                <ul>
                {this.getField("responses")}
                </ul>
                <div/>
                <button id="addProgram" onClick={()=>{this.props.firebase.doAddProgram("test program", this.setStatus.bind(this));}}>Add a program</button>
                <button id="delProgram" onClick={()=>{this.props.firebase.doDeleteProgram("test program", this.setStatus.bind(this))}}>Delete a program</button>
            </div>
        );
    }
}

export default Fields;
