import React from 'react';
import Firebase, { FirebaseContext } from '../firebase';
import SignIn from '../signIn';
import SignOut from '../signOut';
import Admin from '../admin';
import Account from '../account';


class Home extends React.Component{
    constructor(){
        super();
        this.state = {
            userSignedIn: false,
            user: "",
            isAdmin: false
        };
    }
    signInUser(newUser, adminStatus){
        console.log(newUser);
        this.setState({
            userSignedIn: true,
            user: newUser,
            isAdmin: adminStatus
        });
    }
    signOutUser(){
        this.setState({
            userSignedIn: false,
            user: "",
            isAdmin: false
        });
    }
    render(){
        var adminPage = ""
        var accountPage = ""
        if(this.state.userSignedIn)
        {
            accountPage = <Account userName = {this.state.user}></Account>
        }
        if(this.state.isAdmin)
        {
            adminPage = <Admin />
        }
        return (
            <div>
                <FirebaseContext.Consumer>
                    {
                        firebase => 
                            <SignIn firebase = {firebase} signInBind = {this.signInUser.bind(this)}/>

                    }
                </FirebaseContext.Consumer>
                <FirebaseContext.Consumer>
                    {
                        firebase => 
                            <SignOut firebase = {firebase} signOutBind = {this.signOutUser.bind(this)}/>

                    }
                </FirebaseContext.Consumer>
                {accountPage}
                {adminPage}
            </div>

        );
    }
}

export default Home;
