import React from 'react';
import {connect} from 'react-redux';
import { timeout } from 'q';

class SignIn extends React.Component{
    constructor()
    {
        super();
        setTimeout(() =>{this.props.firebase.checkStatus(this.props.signInBind)}, 650);
    }
    render(){
        timeout();
        return(
            <div>
                <button id="signIn" onClick={() => {this.props.firebase.doSignIn(this.props.signInBind)}}>Sign in!</button>
                <button id="signInTest" onClick={() => {this.props.userSignIn({email: "connected", userName: "works", isAdmin: true})}}>Redux test</button>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
	//return object with key value pairs that this component can use
	// localPropName: globalStateNameFromRedux
	return {
        user: state.userAuthReducer
	};
};
const mapDispatchToProps = (dispatch) => {
	//return object with key value pairs that this component can use
	// localPropName: globalStateNameFromRedux
	return {
            userSignIn: (userObj) => {dispatch({
                type: "SIGNIN",
                payload: userObj})},
            userSignOut: () => {dispatch({
                type: "SIGNOUT"
            });
        }
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(SignIn);
//export default SignIn;
