import React from 'react';
import {connect} from 'react-redux';
import {FirebaseContext} from '../firebase';
import Fields from '../fieldBar';
class Admin extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            statusStr: "Status: no action yet taken",
            responseList: [],
            responseQuery: ""
        };
        setTimeout(this.init.bind(this), 200);
    }
    init()
    {
        this.setSearch("all");
    }
    setStatus(newStatus)
    {
        this.setState({statusStr: "Status: "+newStatus});
    }
    updateList(newElement, clearList=false)
    {
        //updates the list, clearing it instead if clearList = true
        var newList;
        if(clearList)
        {
            newList = []
        }
        else
        {
            newList = this.state.responseList;
            newList.push(newElement);

        }
        this.setState({responseList: newList});
    }
    getField(field)
    {
        //returns the react elements to render the list of the given field
        const fieldList = this.state.responseList;
        const fieldItems = fieldList.map((item) => <li key={item}>{item}</li>)
         return fieldItems;
    }
    setSearch(field, event)
    {
        //sets the search value of the given field
        if(field === "all")
        {
            window.document.getElementById("searchResponse").click();

        }
        else if(field === "response")
        {
            window.document.getElementById("searchResponse").click();
        }

    }
    render(){
        return(

        <div>
            <div>
                <p>
                   Page only for admins to see
                </p>
                <p id="statusInformer">{this.state.statusStr}</p>
            </div>

            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addAdmin" onClick={()=>{firebase.doAddAdmin("test.AdminsEmail@gmail.com", this.setStatus.bind(this));}}>Add an admin</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delAdmin" onClick={()=>{firebase.doDeleteAdmin("test.AdminsEmail@gmail.com", this.setStatus.bind(this))}}>Delete an admin</button>
                }
            </FirebaseContext.Consumer>
            <div></div>
            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addUser" onClick={()=>{firebase.doAddUser("test.UserEmails@gmail.com", this.setStatus.bind(this));}}>Add an User</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delUser" onClick={()=>{firebase.doDeleteUser("test.UserEmails@gmail.com", this.setStatus.bind(this))}}>Delete an User</button>
                }
            </FirebaseContext.Consumer>
            <div></div>
            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addIssue" onClick={()=>{firebase.doAddIssue("test issue", this.setStatus.bind(this));}}>Add an issue</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delIssue" onClick={()=>{firebase.doDeleteIssue("test issue", this.setStatus.bind(this))}}>Delete an issue</button>
                }
            </FirebaseContext.Consumer>
            <div/>
            <hr/>
            <p>Responses</p>
            <input id="resSearchInput"type="text" placeholder="search" onChange={(event)=>{this.setSearch("response",event)}}></input>
            <div />
            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addResponse" onClick={()=>{firebase.doAddResponse("test response", this.setStatus.bind(this));}}>Add a response</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delResponse" onClick={()=>{firebase.doDeleteResponse("test response", this.setStatus.bind(this))}}>Delete a response</button>
                }
            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="searchResponse" onClick={()=>{firebase.doSearch({type:"response", substr:window.document.getElementById("resSearchInput").value, updateListBind: this.updateList.bind(this)}, this.setStatus.bind(this))}}>search responses</button>
                }
            </FirebaseContext.Consumer>
            <ul>
                {this.getField("responses")}
            </ul>
            <div></div>
            <FirebaseContext.Consumer>
                {
                    firebase =>
                        <button id="addProgram" onClick={()=>{firebase.doAddProgram("test program", this.setStatus.bind(this));}}>Add a program</button>
                }

            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <button id="delProgram" onClick={()=>{firebase.doDeleteProgram("test program", this.setStatus.bind(this))}}>Delete a program</button>
                }
            </FirebaseContext.Consumer>
            <FirebaseContext.Consumer>
                {
                    firebase => 
                    <Fields/>
                }
            </FirebaseContext.Consumer>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
	//return object with key value pairs that this component can use
	// localPropName: globalStateNameFromRedux
	return {
        fields: state.fieldReducer
	};
};
const mapDispatchToProps = (dispatch) => {
	//return object with key value pairs that this component can use
	// localPropName: function that will call redux store dispatch
	return {
            changeSearch: (searchType, newQuery) => {dispatch({
                type: searchType,
                payload: newQuery})},
            resetSearches: () => {dispatch({
                type: "CLEARALLSEARCHES"
            });
        }
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(Admin);
